import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getProfile } from '../services/api';
import { useSelector } from 'react-redux';
import './Dashboard.css';  // You can style the dashboard with CSS

function Dashboard() {
  const [userInfo, setUserInfo] = useState(null);
  const navigate = useNavigate();
  const isAuthenticated = useSelector((state) => state.user.isAuthenticated);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
    } else {
      fetchUserProfile();
    }
  }, [isAuthenticated, navigate]);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await getProfile(token);
      setUserInfo(response.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="dashboard-container">
      <h2>Dashboard</h2>
      {userInfo ? (
        <div className="dashboard-content">
          <div className="welcome-section">
            <h3>Welcome, {userInfo.username}!</h3>
            <p>Email: {userInfo.email}</p>
          </div>

          {/* User-specific Stats */}
          <div className="user-stats">
            <h4>Your Stats</h4>
            <ul>
              <li><strong>Joined Study Groups:</strong> 3</li> {/* You can fetch real data for this */}
              <li><strong>Upcoming Events:</strong> 2</li> {/* Fetch upcoming events */}
              <li><strong>Total Messages:</strong> 45</li> {/* Placeholder stats */}
            </ul>
          </div>

          {/* Quick Links */}
          <div className="quick-links">
            <h4>Quick Links</h4>
            <button onClick={() => navigate('/study-group')}>View Study Groups</button>
            <button onClick={() => navigate('/profile')}>Edit Profile</button>
            <button onClick={() => navigate('/settings')}>Settings</button>
            <button onClick={handleLogout}>Logout</button>
          </div>

          {/* Recent Activity Section */}
          <div className="recent-activity">
            <h4>Recent Activity</h4>
            <ul>
              <li>Joined "Math Study Group" on Oct 15, 2024</li>
              <li>Posted a message in "Physics Study Group" on Oct 14, 2024</li>
              <li>Attended "Python Workshop" on Oct 12, 2024</li>
            </ul>
          </div>
        </div>
      ) : (
        <p>Loading user information...</p>
      )}
    </div>
  );
}

export default Dashboard;
