import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudyGroup = () => {
    const [groups, setGroups] = useState([]); // State to hold all study groups
    const [error, setError] = useState(''); // State to store error messages

    useEffect(() => {
        const fetchGroups = async () => {
            try {
                const response = await axios.get('/api/study-group', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
                });
                setGroups(response.data.study_groups); // Access the study_groups from the response

                // Fetch members for each group
                const updatedGroups = await Promise.all(response.data.study_groups.map(async (group) => {
                    const membersResponse = await axios.get(`/api/study-group/${group.id}/members`, {
                        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
                    });
                    return { ...group, members: membersResponse.data.members }; // Add members to the group
                }));

                setGroups(updatedGroups);
            } catch (error) {
                console.error('Failed to fetch groups:', error);
                setError('Failed to fetch study groups.');
            }
        };

        fetchGroups();
    }, []);

    const handleJoinGroup = async (groupId) => {
        try {
            await axios.post(`/api/study-group/${groupId}/join`, {}, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            // Optionally update the UI to reflect the new member
            setGroups((prevGroups) =>
                prevGroups.map(group =>
                    group.id === groupId ? { ...group, members: [...group.members, { id: 'new_member_id', name: 'Your Name' }] } : group
                )
            );
        } catch (error) {
            console.error('Failed to join group:', error);
            setError('Failed to join the study group.');
        }
    };

    const handleLeaveGroup = async (groupId) => {
        try {
            await axios.post(`/api/study-group/${groupId}/leave`, {}, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            // Optionally update the UI to reflect the removed member
            setGroups((prevGroups) =>
                prevGroups.map(group =>
                    group.id === groupId ? { ...group, members: group.members.filter(member => member.id !== 'your_member_id') } : group
                )
            );
        } catch (error) {
            console.error('Failed to leave group:', error);
            setError('Failed to leave the study group.');
        }
    };

    return (
        <div>
            <h2>Your Study Groups</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error message if any */}
            {groups.length > 0 ? (
                <ul>
                    {groups.map((group) => (
                        <li key={group.id}>
                            <h3>{group.name}</h3>
                            <p>{group.description}</p>
                            <h4>Members:</h4>
                            <ul>
                                {group.members.map(member => (
                                    <li key={member.id}>{member.name}</li>
                                ))}
                            </ul>
                            <button onClick={() => handleJoinGroup(group.id)}>Join Group</button>
                            <button onClick={() => handleLeaveGroup(group.id)}>Leave Group</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No study groups found.</p> // Message if there are no groups
            )}
        </div>
    );
};

export default StudyGroup;
