import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createStudyGroup } from '../services/api';

function CreateStudyGroup() {
  const [groupName, setGroupName] = useState('');
  const [groupDescription, setGroupDescription] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleCreateGroup = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      const response = await createStudyGroup(token, groupName, groupDescription);

      if (response.status === 201) {
        navigate('/study-group'); // Redirect to the study group page after successful creation
      } else {
        setError('Failed to create study group. Please try again.');
      }
    } catch (error) {
      console.error('Error creating study group:', error);
      setError('An error occurred while creating the study group.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-8 max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-4">Create a Study Group</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <form onSubmit={handleCreateGroup}>
        <div className="mb-4">
          <label htmlFor="groupName" className="block text-sm font-medium text-gray-700">Group Name</label>
          <input
            type="text"
            id="groupName"
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
            className="mt-1 p-2 block w-full border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="groupDescription" className="block text-sm font-medium text-gray-700">Group Description</label>
          <textarea
            id="groupDescription"
            value={groupDescription}
            onChange={(e) => setGroupDescription(e.target.value)}
            className="mt-1 p-2 block w-full border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
            rows="4"
            required
          />
        </div>
        <button
          type="submit"
          className={`bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 ${loading ? 'opacity-50' : ''}`}
          disabled={loading}
        >
          {loading ? 'Creating...' : 'Create Study Group'}
        </button>
      </form>
    </div>
  );
}

export default CreateStudyGroup;
