import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudyGroup = () => {
    const [groups, setGroups] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true); // Loading state

    useEffect(() => {
        const fetchGroups = async () => {
            setLoading(true); // Start loading
            try {
                const response = await axios.get('http://localhost:5000/study-group', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
                });
                // Initialize showMembers to false
                const groupsWithShowMembers = response.data.study_groups.map(group => ({
                    ...group,
                    showMembers: false,
                }));
                setGroups(groupsWithShowMembers);
            } catch (error) {
                if (error.response) {
                    console.error('Failed to fetch groups:', error.response.data);
                    setError(error.response.data.message || 'Failed to fetch study groups.');
                } else {
                    console.error('Failed to fetch groups:', error.message);
                    setError('Failed to fetch study groups.');
                }
            } finally {
                setLoading(false); // End loading
            }
        };

        fetchGroups();
    }, []);

    const joinGroup = async (groupId) => {
        try {
            const response = await axios.post('http://localhost:5000/study-group/join', { groupId });
            console.log('Successfully joined group:', response.data);
            // Optionally, update the state if you want to reflect new members
            setGroups((prevGroups) =>
                prevGroups.map((group) => {
                    if (group.id === groupId) {
                        return { ...group, members: [...group.members, response.data.newMember] }; // Assuming response returns the new member
                    }
                    return group;
                })
            );
        } catch (error) {
            console.error('Failed to join group:', error);
            setError('Failed to join the study group.');
        }
    };

    const leaveGroup = async (groupId) => {
        try {
            const response = await axios.post('http://localhost:5000/study-group/leave', 
            { groupId }, 
            {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });

            // Update the group after successfully leaving
            setGroups((prevGroups) =>
                prevGroups.map((group) => {
                    if (group.id === groupId) {
                        return { ...group, members: response.data.members };
                    }
                    return group;
                })
            );
        } catch (error) {
            if (error.response) {
                console.error('Failed to leave group:', error.response.data);
                setError(error.response.data.message || 'Failed to leave the study group.');
            } else {
                console.error('Failed to leave group:', error.message);
                setError('Failed to leave the study group.');
            }
        }
    };

    const toggleMembers = (groupId) => {
        setGroups((prevGroups) =>
            prevGroups.map((group) => {
                if (group.id === groupId) {
                    return { ...group, showMembers: !group.showMembers };
                }
                return group;
            })
        );
    };

    if (loading) {
        return <p>Loading...</p>; // Loading feedback
    }

    return (
        <div>
            <h2>Your Study Groups</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {groups.length > 0 ? (
                <ul>
                    {groups.map((group) => (
                        <li key={group.id}>
                            <h3>{group.name}</h3>
                            <button onClick={() => joinGroup(group.id)}>Join Group</button>
                            <button onClick={() => leaveGroup(group.id)}>Leave Group</button>
                            <button onClick={() => toggleMembers(group.id)}>
                                {group.showMembers ? 'Hide Members' : 'Show Members'}
                            </button>
                            {group.showMembers && group.members && group.members.length > 0 && (
                                <div>
                                    <h4>Members:</h4>
                                    <ul>
                                        {group.members.map((member) => (
                                            <li key={member.id}>{member.username}</li>
                                        ))}
                                    </ul>
                                </div>
                            )}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No study groups found.</p>
            )}
        </div>
    );
};

export default StudyGroup;
