import React, { useEffect, useState } from 'react';

function StudyGroup({ groupId }) {
  const [group, setGroup] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch group data
  useEffect(() => {
    let isMounted = true;  // Flag to track if the component is still mounted
    const fetchGroupData = async () => {
      try {
        const response = await fetch(`/api/groups/${groupId}`);
        const data = await response.json();

        if (isMounted) {
          setGroup(data);
          setLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError(err.message);
          setLoading(false);
        }
      }
    };

    fetchGroupData();

    // Cleanup function to avoid memory leak
    return () => {
      isMounted = false;
    };
  }, [groupId]);

  const joinGroup = async () => {
    try {
      const response = await fetch(`/api/groups/${groupId}/join`, { method: 'POST' });
      const result = await response.json();

      if (!result.success) {
        throw new Error(result.message);
      }
      alert('Joined the group successfully!');
    } catch (err) {
      console.error('Failed to join group:', err);
      alert(`Failed to join group: ${err.message}`);
    }
  };

  if (loading) return <p>Loading group data...</p>;
  if (error) return <p>Error loading group: {error}</p>;
  if (!group) return <p>Group not found.</p>;

  return (
    <div>
      <h2>{group.name}</h2>
      <ul>
        {group.members && group.members.length > 0 ? (
          group.members.map((member, index) => (
            // Safeguard for undefined or null members
            member ? (
              <li key={index}>{member.username || 'Unknown User'}</li>
            ) : (
              <li key={index}>Unknown User</li>
            )
          ))
        ) : (
          <p>No members found.</p>
        )}
      </ul>
      <button onClick={joinGroup}>Join Group</button>
    </div>
  );
}

export default StudyGroup;
